    private lateinit var set: String
    private lateinit var language: String
    private var interstitial: InterstitialAd? = null

    override fun onCreate(savedInstanceState: Bundle?) {

        super.onCreate(savedInstanceState)
        binding = ActivityGuessGameBinding.inflate(layoutInflater)
        setContentView(binding.root)

        val bundle: Bundle? = intent.extras
        counter = bundle?.getInt("counter")!!
        language = bundle.getString("language").toString()
        set = bundle.getString("set").toString()

        //Ads
        //loadAds()
        //loadAdsInterstitial()

        binding.counterTV.text = getString(R.string.try_left) + counter

        //Cards
        var cardsList: List<CardsData> = emptyList()

        if (set == "Standard" || set == "Estándar") { //Standard
            if (language == "English" || language == "Inglés") {
                loadCardsStandardEnglish { result ->
                    cardsList = result
                }
            } else {
                loadCardsStandardSpanish { result -> //Spanish
                    cardsList = result
                }
            }
        } else { //Wild
            if (language == "English" || language == "Inglés") {
                loadCardsWildEnglish { result ->
                    cardsList = result
                }
            } else {
                loadCardsWildSpanish { result ->
                    cardsList = result
                }
            }
        }

        Log.d("DEBUG", "\nSet: $set, language: $language")


        val maxRange = cardsList.size
        val random = Random()
        val randomNumber = random.nextInt(0..maxRange)

        cardWinner = cardsList[randomNumber]

        val namesSuggestions = arrayListOf<String>()

        for (i in cardsList) {
            namesSuggestions.add(i.name.toString())
        }


        val adapter =
            ArrayAdapter(this, android.R.layout.simple_expandable_list_item_1, namesSuggestions)
        binding.cardNameACTV.threshold = 0
        binding.cardNameACTV.setAdapter(adapter)

        Log.d("DEBUG", "\nWINNER: $cardWinner")


        binding.submitBTN.setOnClickListener {
            var found = false
            if (binding.cardNameACTV.text != null || !binding.cardNameACTV.text.equals("")) {
                for (i in cardsList) {
                    if (i.name == binding.cardNameACTV.text.toString()) {
                        selectedCard = i
                        found = true

                        counter--
                        if (counter >= 0) {  //Cambiar esto para que no ponga que tienes 0 intentos

                            updateCounter(counter)

                            hideKeyboard()
                            addTable(selectedCard)
                            if (counter <= 0){
                                lose()
                            }
                        } else lose()
                    }
                }
            }
            if (!found) Toast.makeText(this, "Try with a valid card", Toast.LENGTH_SHORT).show()
        }
    }

