    }

    private fun winner(cardUrl: String?) {
        Log.d("DEBUG", "\nCard Url: $cardUrl")

        val intent = Intent(this, WinnerActivity::class.java).apply {
            putExtra("cardUrl", cardUrl)
        }
        startActivity(intent)
    }

    private fun hideKeyboard() {
        val imm = getSystemService(INPUT_METHOD_SERVICE) as InputMethodManager
        imm.hideSoftInputFromWindow(binding.viewRoot.windowToken, 0)
    }

    private fun showAdOrLoose() {

        val view = View.inflate(this, R.layout.dialog_view_error_auth, null)

        val builder = AlertDialog.Builder(this)
        builder.setView(view)

        val dialog: AlertDialog = builder.create()
        dialog.show()
        dialog.window?.setBackgroundDrawableResource(android.R.color.transparent)
        dialog.setCancelable(false)

        view.btn_confirm.setOnClickListener {
            showAds()
            dialog.dismiss()
            loadAdsInterstitial()
            updateCounter(5)
        }
        view.btn_close.setOnClickListener {
            val intent = Intent(this, MainActivity::class.java)
            startActivity(intent)
        }
    }

    private fun loadAds() {
        val adRequest = AdRequest.Builder().build()
        binding.bannerAd.loadAd(adRequest)
    }

    private fun showAds() {
        interstitial?.show(this)
    }

    private fun loadAdsInterstitial() {
        val adRequest = AdRequest.Builder().build()

        InterstitialAd.load(
            this,
            "ADMOB_INSTERSTITIAL_ID",
            adRequest,
            object : InterstitialAdLoadCallback() {
                override fun onAdLoaded(interstitialAd: InterstitialAd) {
                    interstitial = interstitialAd
                }

                override fun onAdFailedToLoad(p0: LoadAdError) {
                    interstitial = null
                }
            })
    }

    private fun updateCounter(counterPassed: Int) {
        counter = counterPassed
        binding.counterTV.text = getString(R.string.try_left) + counterPassed
    }

